import { useRef, useState } from "react";
import Input from "../../UI/input";
import classes from "./mealItemForm.module.css";
const MealItemForm = (props) => {
  const amountInputRef = useRef();
  const [amountisValid, setAmountisValid] = useState(true);
  const submitForm = (e) => {
    e.preventDefault();
    // the value of the current ref.value is always a string, even if the type of the input is number
    const enteredAmount = amountInputRef.current.value;
    const enteredAmountNumber = +enteredAmount;

    if (
      enteredAmount.trim().lenggth === 0 ||
      enteredAmountNumber < 1 ||
      enteredAmountNumber > 50
    ) {
      setAmountisValid(false);
      console.log(enteredAmount, enteredAmountNumber);
      return;
    }

    props.onAddToCart(enteredAmountNumber);
  };
  return (
    <>
      <form className={classes.form}>
        <Input
          ref={amountInputRef}
          label="Quantity"
          input={{
            id: "quantity",
            type: "number",
            min: "1",
            step: "1",
            defaultValue: "1",
          }}
        />
        <button onClick={(e) => submitForm(e)}>+</button>
        {!amountisValid && <p>Please enter a valid amount</p>}
      </form>
    </>
  );
};

export default MealItemForm;
